import {PostDataConfig} from "./PostDataConfig";

/**
 * Request configuration
 */
export interface SyncRequestConfig {
    /** remote port */
    port: number;
    /** remote hostname */
    host: string;
    /** remote path to the file */
    path: string;
    /** method to use */
    method: 'get' | 'post';
    /** post data (if necessary) */
    postData?: PostDataConfig
}