import {SyncRequests} from "./sync-requests/SyncRequests";
import {SyncRequestConfig} from "./sync-requests/SyncRequestConfig";


const config: SyncRequestConfig = {
    method: "get",
    host: "example.com",
    path: "/",
    port: 80
};
const N: number = 16;


(async () => {
    const requests: SyncRequests = new SyncRequests();

    for (let i = 0; i < N; ++ i)
        requests.addRequest(config);

    console.log("Starting requests");
    await requests.start();

    console.log("Terminating requests");
    await requests.terminate();

    console.log("OK");
})();